haykh Al Islam Taqiuddin Ahmed Abdul Halim Ibn Taymiyyah\u0647\u0648 \u0627\u0644\u0625\u0645\u0627\u0645 \u0627\u0644\u0645\u062c\u062a\u0647\u062f \u0634\u064a\u062e \u0627\u0644\u0625\u0633\u0644\u0627\u0645\u060c \u062a\u0642\u064a \u0627\u0644\u062f\u064a\u0646 \u0623\u0628\u0648 \u0627\u0644\u0639\u0628\u0627\u0633 \u0623\u062d\u0645\u0640\u062f \u0627\u0628\u0646 \u0639\u0628\u062f \u0627\u0644\u062d\u0644\u064a\u0640\u0640\u0640\u0645 \u0628\u0646 \u0639\u0628\u062f \u0627\u0644\u0633\u0640\u0640\u0644\u0627\u0645 \u0628\u0646 \u0639\u0628\u0640\u0640\u062f \u0627\u0644\u0644\u0647 \u0628\u0646 \u0627\u0644\u062e\u0636\u0640\u0640\u0640\u0631 \u0628\u0646 \u0645\u062d\u0645\u0640\u0640\u0640\u062f \u0627\u0628\u0646 \u0627\u0644\u062e\u0636\u0631 \u0628\u0646 \u0639\u0644\u064a \u0628\u0646 \u0639\u0628\u062f \u0627\u0644\u0644\u0647 \u0628\u0646 \u062a\u064a\u0645\u064a\u0629 \u0627\u0644\u062d\u0631\u0627\u0646\u064a.\u200f\u0648\u0644\u062f \u0628\u062d\u064e\u0631\u064e\u0651\u0627\u0646 \u064a\u0648\u0645 \u0627\u0644\u0627\u062b\u0646\u064a\u0646 \u0639\u0627\u0634\u0631 \u0631\u0628\u064a\u0639 \u0627\u0644\u0623\u0648\u0644\u060c \u0633\u0646\u0629 \u0625\u062d\u062f\u0649 \u0648\u0633\u062a\u064a\u0646 \u0648\u0633\u062a\u0645\u0627\u0626\u0629\u060c \u0648\u0646\u0634\u0623 \u0641\u064a \u0628\u064a\u0626\u0629 \u0639\u0644\u0645\u064a\u0629\u060c \u0641\u0643\u0627\u0646 \u062c\u062f\u0647 \u0623\u0628\u0648 \u0627\u0644\u0628\u0631\u0643\u0627\u062a \u0639\u0628\u062f \u0627\u0644\u0633\u0644\u0627\u0645 \u0627\u0628\u0646 \u0639\u0628\u062f \u0627\u0644\u0644\u0647\u060c \u0635\u0627\u062d\u0628 \u0643\u062a\u0627\u0628 : (\u0627\u0644\u0645\u0646\u062a\u0642\u0649 \u0645\u0646 \u0623\u062e\u0628\u0627\u0631 \u0627\u0644\u0645\u0635\u0637\u0641\u0649 \u0635\u0644\u0649 \u0627\u0644\u0644\u0647 \u0639\u0644\u064a\u0647 \u0648 \u0633\u0644\u0645)\u060c \u0645\u0646 \u0623\u0626\u0645\u0629 \u0639\u0644\u0645\u0627\u0621 \u0627\u0644\u0645\u0630\u0647\u0628 \u0627\u0644\u062d\u0646\u0628\u0644\u064a\u060c \u0648\u0648\u0627\u0644\u062f\u0647 \u0645\u0646 \u0639\u0644\u0645\u0627\u0621 \u0627\u0644\u0645\u0630\u0647\u0628\u060c \u0627\u0634\u062a\u063a\u0644 \u0628\u0627\u0644\u062a\u062f\u0631\u064a\u0633 \u0648\u0627\u0644\u0641\u062a\u0648\u0649\u060c \u0648\u0648\u0644\u064a \u0645\u0634\u064a\u062e\u0629 \u062f\u0627\u0631 \u0627\u0644\u062d\u062f\u064a\u062b \u0627\u0644\u0633\u0643\u0631\u064a\u0629 \u062d\u062a\u0649 \u0648\u0641\u0627\u062a\u0647.\u200f\u0627\u0646\u062a\u0642\u0644 \u0645\u0639 \u0623\u0633\u0631\u062a\u0647 \u0625\u0644\u0649 \u062f\u0645\u0634\u0642 \u0639\u0644\u0649 \u0625\u062b\u0631 \u062a\u062e\u0631\u064a\u0628 \u0627\u0644\u062a\u062a\u0627\u0631 \u0644\u0628\u0644\u062f\u0647 \u062d\u0631\u0627\u0646\u060c \u0648\u0647\u0648 \u0627\u0628\u0646 \u0633\u0628\u0639 \u0633\u0646\u064a\u0646\u060c \u0648\u0628\u062f\u062a \u0639\u0644\u064a\u0647 \u0645\u062e\u0627\u064a\u0644 \u0627\u0644\u0646\u062c\u0627\u0628\u0629 \u0648\u0627\u0644\u0630\u0643\u0627\u0621 \u0648\u0627\u0644\u0641\u0637\u0646\u0629 \u0645\u0646\u0630 \u0635\u063a\u0631\u0647\u060c \u0641\u062d\u0641\u0638 \u0627\u0644\u0642\u0631\u0622\u0646 \u0641\u064a \u0633\u0646 \u0645\u0628\u0643\u0631\u0629\u060c \u0648\u0644\u0645 \u064a\u062a\u0645 \u0627\u0644\u0639\u0634\u0631\u064a\u0646 \u0625\u0644\u0627 \u0648\u0628\u0644\u063a \u0645\u0646 \u0627\u0644\u0639\u0644\u0645 \u0645\u0628\u0644\u063a\u0647\u060c \u0630\u0643\u0631 \u0627\u0628\u0646 \u0639\u0628\u062f \u0627\u0644\u0647\u0627\u062f\u064a \u0641\u064a \u062a\u0631\u062c\u0645\u062a\u0647 : \u0623\u0646 (\u0634\u064a\u0648\u062e\u0647 \u0627\u0644\u0630\u064a\u0646 \u0633\u0645\u0639 \u0645\u0646\u0647\u0645 \u0623\u0643\u062b\u0631 \u0645\u0646 \u0645\u0627\u0626\u062a\u064a \u0634\u064a\u062e\u060c \u0633\u0645\u0639 \u0645\u0633\u0646\u062f \u0627\u0644\u0625\u0645\u0627\u0645 \u0623\u062d\u0645\u062f \u0628\u0646 \u062d\u0646\u0628\u0644 \u0645\u0631\u0627\u062a\u060c \u0648\u0633\u0645\u0639 \u0627\u0644\u0643\u062a\u0628 \u0627\u0644\u0633\u062a\u0629 \u0627\u0644\u0643\u0628\u0627\u0631 \u0648\u0627\u0644\u0623\u062c\u0632\u0627\u0621\u060c \u0648\u0645\u0646 \u0645\u0633\u0645\u0648\u0639\u0627\u062a\u0647 \u0645\u0639\u062c\u0645 \u0627\u0644\u0637\u0628\u0631\u0627\u0646\u064a \u0627\u0644\u0643\u0628\u064a\u0631\u060c \u0648\u0639\u064f\u0646\u064a \u0628\u0627\u0644\u062d\u062f\u064a\u062b\u060c \u0648\u0642\u0631\u0623 \u0648\u0646\u0633\u062e \u0648\u062a\u0639\u0644\u0645 \u0627\u0644\u062e\u0637 \u0648\u0627\u0644\u062d\u0633\u0627\u0628 \u0641\u064a \u0627\u0644\u0645\u0643\u062a\u0628\u060c \u0648\u062d\u0641\u0638 \u0627\u0644\u0642\u0631\u0622\u0646\u060c \u0648\u0623\u0642\u0628\u0644 \u0639\u0644\u0649 \u0627\u0644\u0641\u0642\u0647\u060c \u0648\u0642\u0631\u0623 \u0627\u0644\u0639\u0631\u0628\u064a\u0629 \u0639\u0644\u0649 \u0627\u0628\u0646 \u0639\u0628\u062f \u0627\u0644\u0642\u0648\u064a\u060c \u062b\u0645 \u0641\u0647\u0645\u0647\u0627\u060c \u0648\u0623\u062e\u0630 \u064a\u062a\u0623\u0645\u0644 \u0643\u062a\u0627\u0628 \u0633\u064a\u0628\u0648\u064a\u0647\u060c \u062d\u062a\u0649 \u0641\u0647\u0645 \u0641\u064a \u0627\u0644\u0646\u062d\u0648\u060c \u0648\u0623\u0642\u0628\u0644 \u0639\u0644\u0649 \u0627\u0644\u062a\u0641\u0633\u064a\u0631 \u0625\u0642\u0628\u0627\u0644\u0627\u064b \u0643\u0644\u064a\u064b\u0627\u060c \u062d\u062a\u0649 \u062d\u0627\u0632 \u0641\u064a\u0647 \u0642\u064e\u0635\u064e\u0628 \u0627\u0644\u0633\u064e\u0651\u0628\u0642\u060c \u0648\u0623\u062d\u0643\u0645 \u0623\u0635\u0648\u0644 \u0627\u0644\u0641\u0642\u0647 \u0648\u063a\u064a\u0631 \u0630\u0644\u0643\u060c \u0647\u0630\u0627 \u0643\u0644\u0647 \u0648\u0647\u0648 \u0628\u0639\u062f \u0627\u0628\u0646 \u0628\u0636\u0639 \u0639\u0634\u0631\u0629 \u0633\u0646\u0629\u060c \u0641\u0627\u0646\u0628\u0647\u0631 \u0623\u0647\u0644 \u062f\u0645\u0634\u0642 \u0645\u0646 \u0641\u0631\u0637 \u0630\u0643\u0627\u0626\u0647\u060c \u0648\u0633\u064a\u0644\u0627\u0646 \u0630\u0647\u0646\u0647\u060c \u0648\u0642\u0648\u0629 \u062d\u0627\u0641\u0638\u062a\u0647\u060c \u0648\u0633\u0631\u0639\u0629 \u0625\u062f\u0631\u0627\u0643\u0647) .\u200f\u0623\u0641\u062a\u0649 \u0648\u0644\u0647 \u062a\u0633\u0639 \u0639\u0634\u0631\u0629 \u0633\u0646\u0629\u060c \u0648\u0634\u0631\u0639 \u0641\u064a \u0627\u0644\u062a\u0623\u0644\u064a\u0641 \u0648\u0647\u0648 \u0627\u0628\u0646 \u0647\u0630\u0627 \u0627\u0644\u0633\u0646\u060c \u0648\u062a\u0648\u0644\u0649 \u0627\u0644\u062a\u062f\u0631\u064a\u0633 \u0628\u0639\u062f \u0648\u0641\u0627\u0629 \u0648\u0627\u0644\u062f\u0647\u060c \u0633\u0646\u0629 682\u0647\u0640 \u0628\u062f\u0627\u0631 \u0627\u0644\u062d\u062f\u064a\u062b \u0627\u0644\u0633\u0643\u0631\u064a\u0629\u060c \u0648\u0644\u0647 \u0625\u062d\u062f\u0649 \u0648\u0639\u0634\u0631\u0648\u0646 \u0633\u0646\u0629\u060c \u062d\u062a\u0649 \u0627\u0634\u062a\u0647\u0631 \u0623\u0645\u0631\u0647 \u0628\u064a\u0646 \u0627\u0644\u0646\u0627\u0633\u060c \u0648\u0628\u0639\u062f \u0635\u064a\u062a\u0647 \u0641\u064a \u0627\u0644\u0622\u0641\u0627\u0642\u060c \u0646\u0638\u0631\u064b\u0627 \u0644\u063a\u0632\u0627\u0631\u0629 \u0639\u0644\u0645\u0647 \u0648\u0633\u0639\u0629 \u0645\u0639\u0631\u0641\u062a\u0647\u060c \u0641\u0642\u062f \u062e\u0635\u0647 \u0627\u0644\u0644\u0647 \u0628\u0627\u0633\u062a\u0639\u062f\u0627\u062f \u0630\u0627\u062a\u064a \u0623\u0647\u0651\u0644\u0647 \u0644\u0630\u0644\u0643\u060c \u0645\u0646\u0647 \u0642\u0648\u0629 \u0627\u0644\u062d\u0627\u0641\u0638\u0629\u060c \u0648\u0625\u0628\u0637\u0627\u0621 \u0627\u0644\u0646\u0633\u064a\u0627\u0646\u060c \u0641\u0644\u0645 \u064a\u0643\u0646 \u064a\u0642\u0641 \u0639\u0644\u0649 \u0634\u064a\u0621 \u0623\u0648 \u064a\u0633\u062a\u0645\u0639 \u0644\u0634\u064a\u0621 \u0625\u0644\u0627 \u0648\u064a\u0628\u0642\u0649 \u063a\u0627\u0644\u0628\u064b\u0627 \u0639\u0644\u0649 \u062e\u0627\u0637\u0631\u0647\u060c \u0625\u0645\u0627 \u0628\u0644\u0641\u0638\u0647 \u0623\u0648 \u0645\u0639\u0646\u0627\u0647. \u0641\u0641\u064a \u0645\u062d\u0646\u062a\u0647 \u0627\u0644\u0623\u0648\u0644\u0649 \u0628\u0645\u0635\u0631\u060c \u0635\u0646\u0641 \u0639\u062f\u0629 \u0643\u062a\u0628 \u0648\u0647\u0648 \u0628\u0627\u0644\u0633\u062c\u0646\u060c \u0627\u0633\u062a\u062f\u0644 \u0641\u064a\u0647\u0627 \u0628\u0645\u0627 \u0627\u062d\u062a\u0627\u062c \u0625\u0644\u064a\u0647 \u0645\u0646 \u0627\u0644\u0623\u062d\u0627\u062f\u064a\u062b \u0648\u0627\u0644\u0622\u062b\u0627\u0631\u060c \u0648\u0630\u0643\u0631 \u0641\u064a\u0647\u0627 \u0623\u0642\u0648\u0627\u0644 \u0627\u0644\u0645\u062d\u062f\u062b\u064a\u0646 \u0648\u0627\u0644\u0641\u0642\u0647\u0627\u0621\u060c \u0648\u0639\u0632\u0627\u0647\u0627 \u0625\u0644\u0649 \u0642\u0627\u0626\u0644\u064a\u0647\u0627 \u0628\u0623\u0633\u0645\u0627\u0626\u0647\u0645\u060c \u0643\u0644 \u0630\u0644\u0643 \u0628\u062f\u064a\u0647\u0629 \u0627\u0639\u062a\u0645\u0627\u062f\u064b\u0627 \u0639\u0644\u0649 \u062d\u0641\u0638\u0647\u060c \u0641\u0644\u0645\u0627 \u0631\u0648\u062c\u0639\u062a \u0644\u0645 \u064a\u0639\u062b\u0631 \u0641\u064a\u0647\u0627 \u0639\u0644\u0649 \u062e\u0637\u0623 \u0648\u0644\u0627 \u062e\u0644\u0644.\u200f\u0642\u0636\u0649 \u062d\u064a\u0627\u062a\u0647 \u0641\u064a \u0627\u0644\u062a\u062f\u0631\u064a\u0633 \u0648\u0627\u0644\u0641\u062a\u0648\u0649 \u0648\u0627\u0644\u062a\u0623\u0644\u064a\u0641 \u0648\u0627\u0644\u062c\u0647\u0627\u062f\u060c \u0641\u0643\u0627\u0646\u062a \u062a\u0641\u062f \u0625\u0644\u064a\u0647 \u0627\u0644\u0648\u0641\u0648\u062f \u0644\u0633\u0645\u0627\u0639 \u062f\u0631\u0648\u0633\u0647\u060c \u0648\u062a\u064e\u0631\u0650\u062f\u064f\u0647 \u0627\u0644\u0631\u0633\u0627\u0626\u0644 \u0644\u0644\u0627\u0633\u062a\u0641\u062a\u0627\u0621 \u0641\u064a \u0645\u0633\u0627\u0626\u0644 \u0627\u0644\u0639\u0642\u064a\u062f\u0629 \u0648\u0627\u0644\u0634\u0631\u064a\u0639\u0629\u060c \u0641\u064a\u062c\u064a\u0628 \u0639\u0644\u064a\u0647\u0627 \u0643\u062a\u0627\u0628\u0629.. \u062a\u0631\u0643 \u062b\u0631\u0648\u0629 \u0639\u0644\u0645\u064a\u0629 \u062a\u062f\u0644 \u0639\u0644\u0649 \u063a\u0632\u0627\u0631\u0629 \u0639\u0644\u0645\u0647 \u0648\u0633\u0639\u0629 \u0627\u0637\u0644\u0627\u0639\u0647\u060c \u0648\u062a\u0643\u0627\u0645\u0644 \u0625\u062f\u0631\u0627\u0643\u0647 \u0644\u0623\u0637\u0631\u0627\u0641 \u0645\u0627 \u064a\u0628\u062d\u062b\u0647 \u0648\u0627\u0633\u062a\u0648\u0627\u0626\u0647 \u0644\u062f\u064a\u0647\u060c \u0648\u0645\u0646 \u0630\u0644\u0643 \u0645\u0633\u0627\u0626\u0644 \u0639\u0644\u0645 \u0627\u0644\u0643\u0644\u0627\u0645 \u0648\u0645\u0628\u0627\u062d\u062b \u0627\u0644\u0641\u0644\u0633\u0641\u0629\u060c \u0641\u0647\u0648 \u064a\u0646\u0627\u0642\u0634 \u0627\u0644\u0645\u062a\u0643\u0644\u0645\u064a\u0646 \u0648\u0627\u0644\u0641\u0644\u0627\u0633\u0641\u0629 \u0628\u0623\u062f\u0644\u062a\u0647\u0645\u060c \u0648\u064a\u0646\u0642\u062f \u0645\u0646\u0627\u0647\u062c\u0647\u0645\u060c \u0648\u064a\u0628\u0637\u0644 \u062d\u062c\u062c\u0647\u0645 \u0628\u062b\u0642\u0629 \u0648\u0639\u0644\u0645\u060c \u0630\u0643\u0631 \u0627\u0628\u0646 \u0639\u0628\u062f \u0627\u0644\u0647\u0627\u062f\u064a \u0623\u0646 \u0645\u0635\u0646\u0641\u0627\u062a\u0647 \u0648\u0641\u062a\u0627\u0648\u0627\u0647 \u0648\u0631\u0633\u0627\u0626\u0644\u0647 \u0644\u0627 \u064a\u0645\u0643\u0646 \u0636\u0628\u0637 \u0639\u062f\u062f\u0647\u0627\u060c \u0648\u0623\u0646\u0647 \u0644\u0627 \u064a\u064e\u0639\u0644\u0645 \u0623\u062d\u062f\u064b\u0627 \u0645\u0646 \u0645\u062a\u0642\u062f\u0645\u064a \u0627\u0644\u0623\u0645\u0629 \u062c\u0645\u0639 \u0645\u062b\u0644 \u0645\u0627 \u062c\u0645\u0639\u0647\u060c \u0648\u0635\u0646\u0641 \u0646\u062d\u0648 \u0645\u0627 \u0635\u0646\u0641\u0647.\u0634\u0627\u0631\u0643 \u0641\u064a \u0645\u0639\u0631\u0643\u0629 \u0634\u0642\u062d\u0628\u060c \u0627\u0644\u062a\u064a \u0648\u0642\u0639\u062a \u0628\